import sys
import locale

# Získání preferovaného kódování z nastavení systému
encoding = locale.getpreferredencoding()

# Nastavení výchozího kódování na utf-16, pokud je preferované kódování ASCII
#if encoding == 'US-ASCII':
encoding = 'utf-16'

# Nastavení výchozího kódování pro stdin, stdout a stderr
sys.stdout.reconfigure(encoding=encoding)
sys.stderr.reconfigure(encoding=encoding)
sys.stdin.reconfigure(encoding=encoding)

print(f"Výchozí kódování nastaveno na: {encoding}")